    let res = await fetch(url);
    let data = await res.json();
    setProductList(data);
  }
  useEffect(()=>{
    getProducts();
  },[query])

  return (
    <div>
      <Container>
        <Row>{
          productList.map((menu,idx)=>(
            <Col key={idx} md={3}>
              <ProductCard item={menu}/>
            </Col>
          ))
        }</Row>
      </Container>
    </div>
  )
}

export default ProductAll
